import 'dart:developer';

import 'package:observatory/service_io.dart';
import 'service_test_common.dart';
import 'test_helper.dart';

// AUTOGENERATED START
//
// Update these constants by running:
//
// dart pkg/vm_service/test/update_line_numbers.dart runtime/observatory/tests/service/break_on_async_function_test.dart
//
const LINE_A = 20;
// AUTOGENERATED END

Future<String> testFunction() async {  // LINE_A
  await new Future.delayed(new Duration(milliseconds: 1));
  return "Done";
}

testMain() async {
  debugger();
  var str = await testFunction();
  print(str);
}

var tests = <IsolateTest>[
  hasStoppedAtBreakpoint,

// Add breakpoint at the entry of async function
  (Isolate isolate) async {
    Library rootLib = await isolate.rootLibrary.load() as Library;
    var function =
        rootLib.functions.singleWhere((f) => f.name == 'testFunction');

    var bpt = await isolate.addBreakpointAtEntry(function);
    print(bpt);
  },

  resumeIsolate,

  hasStoppedAtBreakpoint,
  stoppedAtLine(LINE_A),
  resumeIsolate,
];

main(args) => runIsolateTests(args, tests, testeeConcurrent: testMain);
